import React, { useState } from 'react';
import axios from 'axios';
import { Box, Button, Container, Skeleton, TextField, Typography } from '@mui/material';
import { useTheme } from '@mui/material/styles';

const PhonePricePredictor = () => {
  const [formData, setFormData] = useState({
    battery_power: '',
    blue: '',
    clock_speed: '',
    dual_sim: '',
    fc: '',
    four_g: '',
    int_memory: '',
    m_dep: '',
    mobile_wt: '',
    n_cores: '',
    px_height: '',
    px_width: '',
    pc: '',
    ram: '',
    sc_h: '',
    sc_w: '',
    talk_time: '',
    three_g: '',
    touch_screen: '',
    wifi: '',
  });

  const [prediction, setPrediction] = useState('');
  const [loading, setLoading] = useState(false);
  const [predictionMade, setPredictionMade] = useState(false);
  const theme = useTheme();

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    
    try {
      const response = await axios.post('https://mobileprice-production.up.railway.app/predict', formData);
      setPrediction(response.data.price_range);
      setPredictionMade(true);
    } catch (error) {
      console.error('Error making prediction', error);
      setPrediction('Error making prediction. Please try again.');
      setPredictionMade(true);
    } finally {
      setLoading(false);
    }
  };

  return (
    <Box
      sx={{
        backgroundColor: '#24DFE5',
        minHeight: '100vh',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        py: 4,
      }}
    >
      <Container 
        maxWidth="sm" 
        sx={{ 
          backgroundColor: '#fff',
          padding: theme.spacing(4),
          borderRadius: theme.shape.borderRadius,
          boxShadow: theme.shadows[3],
          textAlign: 'center',
          width: '100%',
          maxWidth: '600px',
          zIndex: 1
        }}
      >
        {!predictionMade ? (
          <>
            <Typography variant="h4" mb={2}>Phone Price Predictor</Typography>
            <Box 
              component="form" 
              onSubmit={handleSubmit} 
              sx={{ display: 'flex', flexDirection: 'column', gap: 2 }}
            >
              {Object.keys(formData).map((key) => (
                <Box key={key}>
                  {loading ? (
                    <Skeleton variant="rectangular" width="100%" height={56} />
                  ) : (
                    <TextField
                      fullWidth
                      label={key}
                      name={key}
                      value={formData[key]}
                      onChange={handleChange}
                      variant="outlined"
                      InputProps={{
                        sx: {
                          '& .MuiOutlinedInput-root': {
                            '& fieldset': {
                              borderColor: '#24DFE5', // Set the border color to #24DFE5
                            },
                            '&:hover fieldset': {
                              borderColor: '#24DFE5', // Set the border color on hover
                            },
                            '&.Mui-focused fieldset': {
                              borderColor: '#24DFE5', // Set the border color when focused
                            },
                          },
                        },
                      }}
                    />
                  )}
                </Box>
              ))}
              <Button 
                type="submit" 
                variant="contained" 
                sx={{
                  backgroundColor: '#24DFE5', // Button color
                  '&:hover': {
                    backgroundColor: '#19B3B8', // Darker shade on hover
                  },
                  mt: 2
                }}
                disabled={loading}
              >
                {loading ? <Skeleton width="50%" /> : "Predict"}
              </Button>
            </Box>
          </>
        ) : (
          <Typography variant="h5" mt={3} sx={{ display: 'flex', justifyContent: 'center', alignItems: 'center' }}>
            The predicted price for that type of phone would be: 
            <span style={{ fontWeight: 'bold', marginLeft: '8px' }}>{prediction}</span>
          </Typography>
        )}
      </Container>
    </Box>
  );
};

export default PhonePricePredictor;
